import React from 'react';
import './Item.css';

const item = (props) => {
  return (
    <div>
        <div className='item'> 
        <img src={props.image} alt="" />
        <p>{props.name}</p>
        <div className="item-prices">
            <div className="item-price-new">
                ${props.new_price}
            </div>
            <div className="item-price-old ">
                {props.old_price}      
            </div>
        </div>
    </div>
    </div>
  )
}

export default item;
